void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5; // set origo to center of image

    uv.x *= iResolution.x/iResolution.y; // Aspect ratio

    // angle from origo
    float a = atan(uv.y,uv.x);

    // distance from origo 
    float r = length(uv);

    // inverse distance from origo
    float inv_r = 0.3/r; 

    uv = vec2( inv_r + 0.2*iTime, a/3.1415927 );

    fragColor = texture(iChannel0,fract(uv),1.0);
    fragColor *= r; // dark center
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv.x *= iResolution.x/iResolution.y; // Aspect ratio

    uv -= 0.5;

    float blue = 0.5;

    int xor = int(abs(uv.x)*1000.0) ^ int(abs(uv.y)*1000.0);

    blue = float(xor)/1000.0;

    // Output to screen
    fragColor = vec4(blue,0.0,0.0,1.0);
}
