void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0., 0., 0., 1.0);
    float ksq = 8.2 / iResolution.y,
          x0 = (iResolution.x - 8.0/ksq)*0.5,
          y0 = (iResolution.y - 8.0/ksq)*0.5,
          fx = (fragCoord.x - x0) * ksq - 4.0,
          fy = (fragCoord.y - y0) * ksq - 4.0,
          c = cos(iTime*0.3), s = sin(iTime*0.1),
          z = 1.2 / (1.8 - fragCoord.y / iResolution.y),
          rx = 4.0 + z*(fx*c + fy*s),
          ry = 4.0 + z*(fy*c - fx*s);
    int ix = int(floor(rx));
    int iy = int(floor(ry));
    if (ix >= 0 && ix < 8 && iy >= 0 && iy < 8) {
        vec4 n = texture(iChannel0, vec2(rx, ry)*0.25)*0.3-0.1;
        if (ix+iy != (ix + iy)/2*2) {
            fragColor = (n+vec4(0.7, 0.8, 0.9, 1.0))*(1.7-z);
        } else {
            fragColor = (n+vec4(0.5, 0.6, 0.7, 1.0))*(1.7-z);
        }
    }
}
