void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float signal = uv.x;

    float quantizedSignal = round(signal * 8.0)/8.0;

    float error = abs(quantizedSignal-signal);
    error /= 0.25;

    float compare = round(signal * 8.0) / 8.0;

    float outputCol = mix(error, compare, step(0.5, uv.y));

    fragColor = vec4(outputCol,outputCol,outputCol,1.0);
}
