void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 7.*fragCoord/iResolution.xy;

    vec3 col = vec3(0.,0.,0.);

    vec2 p = vec2(1.4,0.5);

    for(float i = 1.; i <9.; i++){
        uv += vec2(
                0.7/i*sin(iTime+i*uv.y+i),
                0.7/i*sin(iTime+i*uv.x+i)
                )*2.;
    }

    fragColor = vec4( vec3(0.6*(sin(uv.x)*0.5+0.5), 0.3*(sin(uv.y)*0.5+0.5), sin(uv.x+uv.y)*0.5+0.5) ,1.0);
}
