float GetDist(vec3 p  ) {	



    float d;
    float dif1;
    float dif2;
    float dif3;
    float dif4;


    d=999.9;

    float planeDist = p.y+3.0;

    vec2 res;
    res=vec2(999.9);


    vec3 pp;
    pp=p;

    vec3 pr;
    pr=p;
    pr.xz*= rotate(iTime * 0.5);


    //piso grande
    float sdbg1= sdBox(  pr- vec3(0,0.5,0), vec3(5,0.5,5));

    //columnas
    float sdbc1= sdBox(  pr- vec3(-4,0.5,4), vec3(0.5,7.0,0.5));
    float sdbc2= sdBox(  pr- vec3(4,0.5,4), vec3(0.5,7.0,0.5));
    float sdbc3= sdBox(  pr- vec3(-4,0.5,-4), vec3(0.5,7.0,0.5));
    float sdbc4= sdBox(  pr- vec3(4,0.5,-4), vec3(0.5,7.0,0.5));


    pp.xz = mod(p.xz, 2.0) - vec2(0.1,1.0);


