    float sdb1= sdBox(  pp- vec3(0,0,0), vec3(1,0.5,1));
    float sdb2= sdBox(  pp- vec3(2,0,0), vec3(1,0.5,1));
    float sdb3= sdBox(  pp- vec3(0,0,1), vec3(1,0.5,1));
    float sdb4= sdBox(  pp- vec3(2,0,1), vec3(1,0.5,1));

