// color variant of https://shadertoy.com/view/3ldBzM

#define D   length( mod( U += T/2. , T ) - R )               //
#define P   clamp( 1.- ( min( D, D ) -.8*R.y )/5. ,0.,1.)    // draw an hexa tiling of disks

void mainImage(out vec4 O, vec2 U) {
    U *= 3.;
    vec2 R = vec2(360)/8., T = vec2( 2, 3.5 )*R;
    float v = P;

    U = 1.1*U.yx + 10.*iTime;
    v += P;

    O =  ( .6 + .6 * cos( 6.3*v/3.  + vec4(0,23,21,0)  ) );  // hue: https://www.shadertoy.com/view/ll2cDc
}
