// Simplex 2D noise
// https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83
vec3 permute(vec3 x){return mod(((x*34.)+1.)*x,289.);}
float snoise(vec2 v){
    const vec4 C=vec4(.211324865405187,.366025403784439,
    -.577350269189626,.024390243902439);
    vec2 i=floor(v+dot(v,C.yy));
    vec2 x0=v-i+dot(i,C.xx);
    vec2 i1;
    i1=(x0.x>x0.y)?vec2(1.,0.):vec2(0.,1.);
    vec4 x12=x0.xyxy+C.xxzz;
    x12.xy-=i1;
    i=mod(i,289.);
    vec3 p=permute(permute(i.y+vec3(0.,i1.y,1.))
    +i.x+vec3(0.,i1.x,1.));
    vec3 m=max(.5-vec3(dot(x0,x0),dot(x12.xy,x12.xy),
    dot(x12.zw,x12.zw)),0.);
    m=m*m;
    m=m*m;
    vec3 x=2.*fract(p*C.www)-1.;
    vec3 h=abs(x)-.5;
    vec3 ox=floor(x+.5);
    vec3 a0=x-ox;
    m*=1.79284291400159-.85373472095314*(a0*a0+h*h);
    vec3 g;
    g.x=a0.x*x0.x+h.x*x0.y;
    g.yz=a0.yz*x12.xz+h.yz*x12.yw;
    return 130.*dot(m,g);
}


// draw a circle
// uv,positon,size
#define drawCircle(st,pos,size)smoothstep(0.,10./iResolution.y,size-length(pos-st))

// smooth staircaise shape
// time, slope, phase
#define smoothStair(x,s,p)x*s-sin(x*p)


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st=fragCoord.xy/iResolution.xy;
    st.x*=iResolution.x/iResolution.y;

    float animation1 = smoothStair(iTime*.3,.5,.5);   
    float animation2 = smoothStair(iTime*.1,3.,2.9);   
    st.x+=snoise(st+vec2(0.,animation1));

    vec3 canvas=vec3(0.); 
    canvas=vec3(step(snoise(st*0.6+animation2),0.5),0.45,0.5);
    float circ= drawCircle(st,vec2(.5),0.35);
    vec3 circColor=vec3(0.0, 1.0, 0.5);

    float circ2= drawCircle(st,vec2(.25),0.35);
    vec3 circColor2=vec3(1.0, 1.0, 0.5);


canvas=mix(canvas,circColor,circ);
canvas=mix(canvas,circColor2,circ2);

    fragColor=vec4(canvas,1.);
}
