//Borrowed rainbow function from u/demofox's Rainbow Shader
vec3 rainbow(float level)
{
	/*
		Target colors
		=============

		L  x   color
		0  0.0 vec4(1.0, 0.0, 0.0, 1.0);
		1  0.2 vec4(1.0, 0.5, 0.0, 1.0);
		2  0.4 vec4(1.0, 1.0, 0.0, 1.0);
		3  0.6 vec4(0.0, 0.5, 0.0, 1.0);
		4  0.8 vec4(0.0, 0.0, 1.0, 1.0);
		5  1.0 vec4(0.5, 0.0, 0.5, 1.0);
	*/

	float r = float(level <= 2.0) + float(level > 4.0) * 0.5;
	float g = max(1.0 - abs(level - 2.0) * 0.5, 0.0);
	float b = (1.0 - (level - 4.0) * 0.5) * float(level >= 4.0);
	return vec3(r, g, b);
}

//v sloppy code, just messin around
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 center = vec2(0.5,0.5);

    //was gonna try to make some different phases where it moves around and stuff 
    //but got lazy and tired and left it as is
    //comment out next line to use it without the multispeaker thing
    uv = mix(mod(1.0 + uv + 0.5*sin(iTime),1.0), uv, min(1.0, floor(mod(iTime,3.0))));

    vec2 diff = uv - center;

    float dist = length(diff);

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float newDist = dist*(0.5+(0.15*sin(iTime*20.)));
    vec3 ymp = vec3(smoothstep(0.1,0.4,newDist));

    float rainbowNum = mod(floor( iTime + (6.0*sin(uv.x)+sin(uv.y))),6.0)/6.0;
    rainbowNum = rainbowNum + mod(iTime,5.0);
    vec3 background = rainbow(rainbowNum);

    vec3 outerspeaker1 = mix(vec3(0.0),background,smoothstep(0.2,0.25,newDist));
    col = mix(ymp,outerspeaker1,step(0.2,newDist) );

    //col = mix(ymp, vec3(0.0), step(0.2, newDist));
     //col = mix(col, vec3(0.0), step(0.2, newDist));
    // Output to screen
    fragColor = vec4(col,1.0);
}
