#define xp(f,a) (pow(6., a* f-a))
#define downthresh 1.04
#define upthresh 1.
#define B 1.5

float drop(vec2 uv, float x, float a, float time){
//    return length(uv.xy-vec2(x, clamp(3.3*a*fract(time), -.5, 1.5)));
//    return length(uv.xy-vec2(x, clamp(3.3*a*fract(time), -1., 5.)-.5));

    return 4.*clamp(length(uv.xy-vec2(x, (6.*fract(time)-.8)*.3*a)),0.,.3);
    //return 4.*clamp(distance(uv.xy, vec2(x, 4.*fract(time)-2.5)),0.,.2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    //uv = 1.-uv;

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    //vec3 col = vec3(1.);

//    float b = drop(uv, 0.6, 1., iTime*0.25);
//         b *= drop(uv, 0.3, 1., .7+iTime*0.25);

   float b = drop(uv, 0.3, 2., iTime*0.25);
         b *= drop(uv, 0.7, 1.4, .4+iTime*0.27);
         b *= drop(uv, 0.1, 1.5, .6+iTime*0.25);
         b *= drop(uv, 0.45, 1.9, .2+iTime*0.26);

    float fb = upthresh-xp(uv.y, 4.);
    fb *= downthresh-xp(1.-uv.y*B, 4.);

    col *= 1.-(b*8.*fb);

    fragColor = vec4(col,1.0);
}
