float remap01(float a,float b,float t)
{
    return (t-a)/(b-a);
}

float sphe(vec3 ro,vec3 rd,vec4 s) 
{
    float t = dot(s.xyz-ro,rd);
    vec3 p = ro + rd*t;
    float y = length(s.xyz-p);  
    if(y<s.w) {
        float x = sqrt(s.w*s.w - y*y);
        float t1 = t-x ;
        float t2= t+x  ;
        float c = remap01(s.z,s.z-s.w,t1);
        return c;
    }
    return 0.;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

   vec3 ro = vec3(0.,0.,0.);
   vec3 rd= normalize(vec3(uv.x,uv.y,1.));


    float s1 = sphe(ro,rd,vec4(.5 + sin(iTime*5.),0.,7.+ cos(iTime*5.),1.));
    float s2 = sphe(ro,rd,vec4(-.5+ sin(iTime*5.),0.,7.+ cos(iTime*5.),1.))  ;
    float s3=0.;
    float s4=0.;
    //PEGI18   :s3 = sphe(ro,rd,vec4(sin(iTime*5.),-.6,6.8+ cos(iTime*5.),.3))  ;
    //FSK18 : s4 = sphe(ro,rd,vec4(sin(iTime*5.),0.,6.8+ cos(iTime*5.),.2))  ;

    float d = max(max(s1,s2),max(s3,s4));

    vec3 col = vec3(d);
    fragColor = vec4(col,1.0);
}
