#ifdef GL_ES
precision mediump float;
#endif

float star(vec2 uv,vec2 pos)
{

    float r = length(pos)*2.0;
    float a = atan(pos.y,pos.x);
    float f= abs(cos(a*2.5))*-0.145+0.40;
   	return 1.-smoothstep(f,f+0.02,r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 st = fragCoord.xy/iResolution.y;
    vec3 color = vec3(0.0);

    vec2 pos = vec2(0.5)-st;

    float r = length(pos)*2.0;
    float a = atan(pos.y,pos.x);

    float f = abs(cos(a*2.5))*0.2+.3;
	float flower=(1.0-smoothstep(f,f+0.02,r));
    color = flower*r*vec3(0.9,0.1,0.6);
	color += vec3(star(st,pos+vec2(0.7,0.0)));
    fragColor = vec4(color, 1.0);
}
