float circle(vec2 pos, vec2 center, float radius)
{
	return step(radius,  length(pos - center));
}

float circleAA(vec2 pos, vec2 center, float radius)
{
    return smoothstep(radius - 0.01, radius + 0.01,  length(pos - center));
}

float radius = 100.0; //pixels 
vec4 circleColor = vec4(1,1,1,0);
vec4 backgroundColor = vec4(0,0,0,0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
	vec2 uv = (2.*fragCoord - iResolution.xy)  / iResolution.y; 

	fragColor = mix(circleColor, backgroundColor, 
                    circle(uv, vec2(0, 0), radius/iResolution.x));
}
