void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float t = abs(cos(iTime));

    if (uv.y > t)
    {
		fragColor = vec4(1.0);
    }
    else
    {
		fragColor = vec4(0.403, 0.647, 0.8, 1.0);
    }

    if (uv.y > t + 0.01)
    {
     	fragColor = vec4(0.0, 0.090, 0.270, 1.0);
    }
}
