#define PI 3.14159

float Circle(vec2 uv, vec2 p, float r) {
	float d = distance(uv, p);
    d = smoothstep(r, r * .99, d);
    return d;
}

vec2 rotate(vec2 v, float a) {
    float s = sin(a);
    float c = cos(a);
    mat2 m = mat2(c, s, -s, c);
    return m * v;
}

float Rect(vec2 uv, vec2 p, vec2 wh, float rot) {
    vec2 sizeH = wh * .5;
    vec2 uvRot = rotate(uv - p, rot);
    float d = distance(uvRot, vec2(clamp(uvRot.x, -sizeH.x, sizeH.x), clamp(uvRot.y, -sizeH.y, sizeH.y)));
    d = smoothstep(.001, .0, d);
    return d;
}

float vecAngle(vec2 a, vec2 b) {
	float cosA = dot(a, b) / length(a) * length(b);
    return acos(cosA);
}

float posSin(float x) {
    return sin(x) * .5 + .5;
}

float posCos(float x) {
    return cos(x) * .5 + .5;
}

float remap(float val, vec2 from, vec2 to) {
    return val / from.x * to.x;
}

float triangle(float x, float p) {
    return 4. / p * (x - p / 2. * floor(2. * x / p + .5)) * pow(-1., floor(2. * x / p + .5));
}

