const float tau = 2.*acos(-1.);

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    //parameters
    float magnitude = 0.25; //from 0 to 1
    float density = 5.0; //number of wave cycles on x-axis
    float speed = 0.5; //frequency (Hz) on x-axis

    vec2 a = fragCoord/iResolution.xy;
    vec2 b = vec2(density, density*iResolution.y/iResolution.x);
    vec2 c = vec2(speed, speed*iResolution.x/iResolution.y);
    vec2 d = magnitude/(tau*b+2.);

    vec2 uv = a*(-2.*d+1.)+d - sin(tau*a*b + iTime*c*tau)*d;
    fragColor = texture(iChannel0, uv);
}
