// https://iquilezles.org/articles/menger


#if HW_PERFORMANCE==0
#define AA 1
#else
#define AA 2  // Set AA to 1 if your machine is too slow
#endif

#define BPM 118.0
#define beat (BPM * iTime / 60.0) 


float maxcomp(in vec3 p ) { return max(p.x,max(p.y,p.z));}
float sdBox( vec3 p, vec3 b )
{
  vec3  di = abs(p) - b;
  float mc = maxcomp(di);
  return min(mc,length(max(di,0.0)));
}

const mat3 ma = mat3( 0.60, 0.00,  0.80,
                      0.00, 1.00,  0.00,
                     -0.80, 0.00,  0.60 );

float map(vec3 p) {
    float d = 1911.0;


    // sbl
    vec3 p_sbl = p - vec3(
        1.0 * abs(fract(1.0 / 2.0 * beat) - 0.5) * 2.0, 
        1.0 * abs(fract(1.0 / 4.0 * beat)- 0.5) * 2.0, 
        1.0 * abs(fract(1.0 / 8.0 * beat)- 0.5) * 2.0
    ).xyz;
    d = min(d, length(p_sbl) - 0.5);



    d = min(d, length(p_sbl - vec3(0.555, 0.5, 0.0) * (1.0 + abs(fract(beat) - 0.5) * 2.0)) - 0.5);

    d = min(d, length(p_sbl - vec3(-0.5, 0.5, 0.0) * (1.0 + 0.5 * abs(fract(beat * 2.0) - 0.5) * 2.0)) - 0.325);





    return d;
}

float intersect( in vec3 ro, in vec3 rd )
{
    float t = 0.0;
	float h = 1.0;
    for( int i=0; i<64; i++ )
    {
		if( h<0.002 || t>10.0 ) break;
        h = map(ro + rd*t);
        t += h;
    }
	if( t>10.0 ) t=-1.0;
    return t;
}

float softshadow( in vec3 ro, in vec3 rd, float mint, float k )
{
    float res = 1.0;
    float t = mint;
	float h = 1.0;
    for( int i=0; i<32; i++ )
    {
        h = map(ro + rd*t);
        res = min( res, k*h/t );
		t += clamp( h, 0.005, 0.1 );
    }
    return clamp(res,0.0,1.0);
}

vec3 calcNormal(in vec3 pos)
{
    vec3  eps = vec3(.001,0.0,0.0);
    vec3 nor;
    nor.x = map(pos+eps.xyy) - map(pos-eps.xyy);
    nor.y = map(pos+eps.yxy) - map(pos-eps.yxy);
    nor.z = map(pos+eps.yyx) - map(pos-eps.yyx);
    return normalize(nor);
}

// light
vec3 light = normalize(vec3(1.0,0.9,0.3));

vec3 render( in vec3 ro, in vec3 rd )
{
    // background color
    vec3 col = texture(iChannel0, rd.xy).rgb;

    float t = intersect(ro,rd);
    if( t>0.0)
    {
        vec3  pos = ro + t*rd;
        vec3  nor = calcNormal(pos);

        nor = floor(nor.xyz * 16.0) / 16.0;

        float occ = 0.0;
		float sha = softshadow( pos, light, 0.01, 64.0 );

		float dif = max(0.1 + 0.9*dot(nor,light),0.0);
		float sky = 0.5 + 0.5*nor.y;
        float bac = max(0.4 + 0.6*dot(nor,vec3(-light.x,light.y,-light.z)),0.0);

        vec3 lin  = vec3(0.0);
        lin += 1.00*dif*vec3(1.10,0.85,0.60)*sha;
        lin += 0.50*sky*vec3(0.10,0.20,0.40)*occ;
        lin += 0.10*bac*vec3(1.00,1.00,1.00)*(0.5+0.5*occ);
        lin += 0.25*occ*vec3(0.15,0.17,0.20);	 

        col = lin;
        col = texture(iChannel0, nor.xy).rgb;
        col = mix(vec3(pow(col.r, 1.0 / 0.5)), col, 1.0) * (0.25  + 0.5 * abs(dot(rd, nor)) + 
                                            10.0 * step(0.5, max(0.0, dot(nor, -rd *vec3(0.5, 0.5, 0.5)))));

        mix(col, vec3(1.0, 1.0, 1.0), step(0.25, max(col.r, max(col.g, col.b))));

        col = max(vec3(0.0), min(vec3(1.0), col * 3.0));
        col = 2.0 * pow(col, vec3(1.0 / 0.5));
    }

    return pow( col, vec3(0.4545) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // camera
    vec3 ro = 1.1*vec3(2.5*sin(0.25*iTime),1.0+1.0*cos(iTime*.13),2.5*cos(0.25*iTime));

#if AA>1
    #define ZERO (min(iFrame,0))
    vec3 col = vec3(0.0);
    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (2.0*(fragCoord+o)-iResolution.xy)/iResolution.y;

        vec3 ww = normalize(vec3(0.0) - ro);
        vec3 uu = normalize(cross( vec3(0.0,1.0,0.0), ww ));
        vec3 vv = normalize(cross(ww,uu));
        vec3 rd = normalize( p.x*uu + p.y*vv + 2.5*ww );

        col += render( ro, rd );
    }
    col /= float(AA*AA);
#else   
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec3 ww = normalize(vec3(0.0) - ro);
    vec3 uu = normalize(cross( vec3(0.0,1.0,0.0), ww ));
    vec3 vv = normalize(cross(ww,uu));
    vec3 rd = normalize( p.x*uu + p.y*vv + 2.5*ww );
    vec3 col = render( ro, rd );
#endif        

    fragColor = vec4(col,1.0);
}


void mainVR( out vec4 fragColor, in vec2 fragCoord, in vec3 fragRayOri, in vec3 fragRayDir )
{
    float time = iTime*0.25 + 0.01*iMouse.x;
    float anim = 1.1 + 0.5*smoothstep( -0.3, 0.3, cos(0.1*iTime) );

    vec3 col = render( fragRayOri + vec3(0.0,1.0,2.5), fragRayDir );

    //col = texture(iChannel0, fragRayDir.xy).rgb;
    //col*=0.0;
    fragColor = vec4( col, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 uv_col_1 = abs(uv - 0.5) * 2.0;

    vec3 col_1 = texture(iChannel0, uv_col_1).rgb;
    vec3 col_bg = texture(iChannel1, uv - vec2(0.0, -1.0) / iResolution.xy).rgb;

    vec3 col = col_1;
    col = mix(col, col_bg * vec3(0.9, 0.0, 0.9), step(0.25, col_1.g));


    fragColor = vec4(col,1.0);
}
