// Trig function errors. This is mainly intended to show what happens on Intel
// where cos and sine are computing with a simple piecewise linear
// approximation. There is a check for GPUs with better trig functions, eg. Nvidia,
// and zooms in vertically if so (it's just checking a single value so might
// give false positives though).

// Red channel: error in cos(x)
// Green channel: error in sin(x)
// Blue channel: ulps error in cos(x)

float PI = 3.141592654;
float mycos(float x) {
  int N = 12;
  float x2 = x*x, y = 1.0;
  for (int n = N; n > 0; n-=2) {
    y = 1.0-x2*y/float(n*(n-1));
  }
  return y;
}


float mysin(float x) {
  int N = 10;
  float x2 = x*x, y = 1.0;
  for (int n = N; n > 0; n-=2) {
    y = 1.0-x2*y/float(n*(n+1));
  }
  return x*y;
}

bool comp(float y, float r) { return (0.0 < y) == (y < r); }

void mainImage(out vec4 fragColor, vec2 fragCoord ) {
  float scale = 1e-4;
  float check = max(0.1,-iTime);
  if (abs(cos(check)-mycos(check)) < 1e-6) scale = 1e-6;
  float x = 0.5*PI*(2.0*fragCoord.x-iResolution.x)/iResolution.x;
  float y = scale * (2.0*fragCoord.y-iResolution.y)/iResolution.y;
  float r = cos(x) - mycos(x);
  float s = sin(x) - mysin(x);
  // The ulps check assumes both values have the same sign.
  int ulps = floatBitsToInt(cos(x))-floatBitsToInt(mycos(x));
  //fragColor = vec4(comp(y,r),comp(y,s),comp(y,float(ulps)/float(1<<23)),1);
  fragColor = vec4(comp(y,r));
}
