vec2 rotate(vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspect = iResolution.x / iResolution.y;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv = -1.0 + 2.0 * uv;
    uv.x *= aspect;

    float adjustAngle = radians(45.0);
    uv = rotate(uv, adjustAngle);

    // tint it blue
    vec3 col = vec3(0.0, 0.1, 0.2);

    // the cross
    col.r += abs(uv.x + uv.y) < (0.15 * aspect) ? 1.0 : 0.0;
    col.r += abs(uv.y - uv.x) < (0.15 * aspect) ? 1.0 : 0.0;
 	col.r = clamp(col.r, 0.0, 1.0);

    // special magic
    col = mix(col, vec3(1.0), 1.0 - col.r);;

    fragColor = vec4(col,1.0);
}
