void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv.y = fract(uv.y*1.);
    uv.y -= .5;

    float T = (iTime + uv.y*3.*uv.x) * 2.5;



    vec3 col = vec3(0);


    float t = uv.x;// + iTime * .2;
    float waves = 5.;
    float s = cos(t * 3.14*2. * waves);
    float h = mix(.0, .05, cos(T+3.14)*.5+.5);

    s = s*h+h;

    float th = .15;//mix(0., .2, uv.x);
    float R = (1.-step(s+th, uv.y));
    float G = (   step(s-th, uv.y));
    float B = (   step(s + (sin(T))*.5*th, uv.y));

    col = vec3(R,G,B);

    // Output to screen
    fragColor = vec4(col,1.0);
}
