vec4 circle(vec2 uv, float maxAngle){
    float r = length(uv);
    float angle = 3.14+atan(uv.y, uv.x);
    if (angle>maxAngle){
     	return vec4(0.);   
    }
    return mix(vec4(1.), vec4(0.), abs(r-1.)*100.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord/iResolution.xy)*2.-vec2(1.);
    uv.x*=iResolution.x/iResolution.y;

    // https://es.wikipedia.org/wiki/Progresi%C3%B3n_geom%C3%A9trica

    // The circle will never be completed, yet it will grow forever
  	// approaching a1/(1-r)

    // With these values it will never reach 359 degrees
    float a1 = 0.5;
    float r = 0.92;

    float n = iTime;
    float Sn = a1 * (pow(r, n) - 1.) / (r - 1.);

    fragColor = circle(uv*1.3, Sn);
}
