void LaplaceStep(  in sampler2D channel, in vec2 fragCoord, in vec2 iResolution, in vec2 ngb, out float count, out vec3 avg )
{
    vec2 uv = (fragCoord + ngb)/iResolution;
    if( uv.x < 0. || uv.x > 1. || uv.y < 0. || uv.y > 1. )
        return;

    vec4 col = texture(channel, uv);
    if( col.w < 0.25 ) 
        return;

    count++;
    avg += col.xyz;
}

vec4 Laplace( in sampler2D channel, in vec2 fragCoord, in vec2 iResolution )
{
    vec2 uv = fragCoord/iResolution;
    vec4 col = texture(channel, uv);
    if( col.w > 0.5 )
        return col;

    float count = 0.;
    vec3 avg = vec3(0.);

    LaplaceStep( channel, fragCoord, iResolution, vec2(-1, 0), count, avg );
    LaplaceStep( channel, fragCoord, iResolution, vec2( 1, 0), count, avg );
    LaplaceStep( channel, fragCoord, iResolution, vec2( 0,-1), count, avg );
    LaplaceStep( channel, fragCoord, iResolution, vec2( 0, 1), count, avg );

    if( count <= 0. )        return col;

    avg /= count;
    if( distance( avg, col.xyz ) < 0.001 )
        return col;

    return vec4(avg, 0.4);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 col;

    if( iFrame < 300 )
        col = texture(iChannel0, uv);
    else
        col = Laplace( iChannel1, fragCoord.xy, iResolution.xy );

    // Output to screen
    fragColor = col;
}
