float rand(float n){
    return fract(cos(n*89.42)*343.42);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 srcCol = texture(iChannel0, uv.xy).xyz;
	vec3 col = srcCol;

    float random = rand(iTime);

    if (abs(uv.y - random) < 0.01) {
        col.x = texture(iChannel0, vec2(uv.x-.2, uv.y)).x;
    }

    fragColor = vec4(col,1.0);
}
