void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(uv.x, uv.y, cos(mod(iTime, float(0.2))));

    vec2 uv_from_mouse = fragCoord - (vec2(0.5)*iResolution.xy);

    float fdistance = sqrt(pow(uv_from_mouse.x, float(2)) + pow(uv_from_mouse.y, float(2)));

    if(fdistance < float(350)){
     	  col = vec3(uv.x, uv.y, pow(tan(iTime*float(4)), float(8))); 
          col = mix(tan(col), vec3(uv.x, uv.y, cos(iTime)), vec3(pow(fdistance, float(2))/float(10000)));
    }

    col = mix(col, vec3(uv.x - 0.1, uv.y - 0.1, -cos(iTime)), float(tan(iTime)));



    // Output to screen
    fragColor = vec4(tan(col*vec3(9))*0.5+vec3(abs(sin(iTime))), 1.0);
}
