#define E 2.7182818284

float N21(vec2 p){
    p = fract(p*vec2(123.45, 678.92));
    p += dot(p, p+23.45);
    return fract(p.x*p.y);
}

vec2 N22(vec2 p){
    float n = N21(p);
    return vec2(n, N21(p+n));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = 5.;
    float aa = scale/iResolution.y;

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	uv *= scale;

    float t = iTime;
    float m = 1.;
    for(float i = 0.; i < 5.; i++){
        vec2 n = N22(vec2(i));
        vec2 p = (n-.5)*scale*.25 + sin(n*t);
        float l = length(p-uv);
        float c = 1.-pow(E,2.-l*3.);
        m *= c;
    }

    float s = mix(-.1, .1, sin(t/3.)*.5+.5);
    m = smoothstep(s, s-aa, m);

    vec3 col = vec3(m);

    // Output to screen
    fragColor = vec4(col,1.0);
}
