// Thanks FabriceNeyret2 for the code and assistance. Awesome.
void mainImage(out vec4 O,vec2 u){
    vec2  R = iResolution.xy;
    float h = R.y, d =   h / ( h + length(u - iMouse.xy) )
                       - h / ( h + length(u - .5*R     ) );
    O[d>0.?1:0] = abs(d) + 1. - h * abs( d - round(d*20.)/20. );}
