void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb;
	vec3 bg_col = texture(iChannel1, uv).rgb;
    vec3 top_col = 0.5 * (texture(iChannel1, uv - vec2(-1., -0.) / iResolution.xy).rgb + texture(iChannel1, uv - vec2(+7., -0.) / iResolution.xy).rgb);
    vec3 bot_col = 0.5 * (texture(iChannel0, uv - vec2(-1., +1.) / iResolution.xy).rgb + texture(iChannel0, uv - vec2(+7., +1.) / iResolution.xy).rgb);

    col = mix(col, top_col, step(-bot_col * 1.2 - 0.25, -top_col) * step(bot_col * 1.0, top_col));

    col = max(vec3(0.0), min(col, vec3(1.0)));
	fragColor = vec4(col, 1.0);
}
