// transparent variant of https://shadertoy.com/view/lsSBWh

void mainImage(out vec4 O, vec2 U) {
    float t = .1*iTime, a,b, n=0.,s,N=0.;
    mat2  R = mat2( sin(t+1.57*vec4(1,2,0,1)) );
    vec3  q = iResolution,
          D = normalize(vec3( U+U-q.xy, -q.y) +.1 ),
          p = 15./q; 

    for ( O+=.5-O; n<2e2 ; n++ )
        q = p,
        q.xz *= R,
        q.yz *= R,
        t = max( length(q) - 9.,                         // Sphere
                 abs(a=cos(b=3.*dot(sin(q),D/D)))/9. ),  // EggCarton
        t < .01 ? s = 4.*max( 0., 1.-.2*N-.005*n ),
                  O.r -= s* b/30., O.g -= s* a, 
                  t=.3, N++ : t, 
        p += t/3.*D;
}
