#define MAX_CIRCLE_SIZE 0.95
#define MIN_CIRCLE_SIZE 0.0
#define CHANNEL iChannel2
#define LINEAR 0
#define DENSITY 120.0
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 color = vec4(0.0,0.0,0.0,0.0);
	float width = DENSITY;
    float height = width;
    width = iResolution.x * 0.001 * width;    
    height = iResolution.y * 0.001 * height;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 position = fragCoord /iResolution.xy;

    position *= vec2(width, height);
    position = ceil(position);
    position /= vec2(width, height);
    vec2 collPosition = position;

    vec4 cellColor = texture(CHANNEL, collPosition);
    cellColor.w = 0.0;
    float brightness = length(cellColor) / 1.7320508075688772;

    #if LINEAR
    float circleSize = clamp((brightness),MIN_CIRCLE_SIZE, MAX_CIRCLE_SIZE);
    #else
     float circleSize = clamp(sqrt(brightness),MIN_CIRCLE_SIZE, MAX_CIRCLE_SIZE);
    #endif

    position -= uv;
    position *= vec2(width, height);
    position -= vec2(1,1) - position;

    vec2 circlePosition = vec2(1.0 - circleSize, 1.0-circleSize);
    circlePosition -= position;
    float distance = length(circlePosition);

    // uv /= vec2(width, height);

	color = cellColor * smoothstep(0.0f,circleSize - distance, 1.);


    fragColor = color;
}
