#define WIDTH 0.2
#define BRITNESS 0.4
#define DEGREE 45.
#define PI 3.1415926535

float line(in vec2 p,  float theta, float t )
{
    float y = tan(theta * PI/180.) * p.x + t;
	float d = y - p.y;
    return smoothstep(-WIDTH/2., 0., d) - smoothstep(0., WIDTH/2., d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb;
    float v = line(uv, DEGREE, sin(iTime));
    if (v < 1.0) {
        col *= 1.+ BRITNESS * v;
    }
    fragColor = vec4(col,1.0);
}
