void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 bufferCol = texture(iChannel0, uv);
	vec4 videoCol = texture(iChannel1, 	uv);
    vec3 finalCol = videoCol.rgb * bufferCol.a;

    fragColor = vec4(finalCol, 1.0);
}

const vec2 GRID_SIZE = vec2(10, 10);

bool inMargin(float a, float b, float margin)
{
    return (a > b && a < b + margin);
}

bool onCell(vec2 uv, vec2 cellCoord)
{
	return (inMargin(uv.x, cellCoord.x, 1.0) && 
            inMargin(uv.y, cellCoord.y, 1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mouseUV = iMouse.xy/iResolution.xy;

    bool written = onCell(mouseUV.xy * GRID_SIZE, floor(uv * GRID_SIZE));

    fragColor = clamp(texture(iChannel0, uv) + vec4(written), .0, 1.0);
}
