void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float speed = 2.0;
    float split = 0.5;

    vec2 uv = fragCoord/iResolution.xy;
	vec4 video = texture(iChannel0, uv);
    float pixel_w = 1.0 / iResolution.x;

    uv.x -= pixel_w * speed;
	vec4 buffer = texture(iChannel1, uv);

  	float effect_mask = step(uv.x, split);

    fragColor = mix(buffer, video, effect_mask); 
}
