int STABMASK(int i){
	return i&255;
}

int PERM(int i){
	return perm[STABMASK(i)];
}

int INDEX(int ix, int iy, int iz){
	return PERM(ix+PERM(iy+PERM(iz)));
}

float Lattice(vec2 coord){
    return float(INDEX(int(floor(coord.x)),
                       int(floor(coord.y)),
                       1)
                )/256.0;
}

float LinearInterpolation(vec2 coord){
	float fx = fract(coord.x);
    float fy = fract(coord.y);
    coord = floor(coord);

    float value00 = Lattice(coord);
    float value01 = Lattice(coord + vec2(0.0,1.0));
    float value11 = Lattice(coord + vec2(1.0,1.0));
    float value10 = Lattice(coord + vec2(1.0,0.0));

    float xValue0 = mix(value00,value10,fx);
    float xValue1 = mix(value01,value11,fx);

    float y = mix(xValue0,xValue1,fy);
    return y;
}

float SmoothstepInterpolation(vec2 coord){
    vec2 fCoord = fract(coord);
    coord = floor(coord);

    float value00 = Lattice(coord);
    float value01 = Lattice(coord + vec2(0.0,1.0));
    float value11 = Lattice(coord + vec2(1.0,1.0));
    float value10 = Lattice(coord + vec2(1.0,0.0));

    vec2 u = fCoord*fCoord*(3.0-2.0*fCoord);

    float xValue0 = mix(value00,value10,u.x);
    float xValue1 = mix(value01,value11,u.x);

    float y = mix(xValue0,xValue1,u.y);
    return y;
}

float CatmullRom(vec4 p, float t){
	vec4 T = vec4(t*t*t,t*t,t,1);
    mat4 M = mat4(vec4(-0.5,1.0,-0.5,0.0),
                  vec4(1.5,-2.5,0.0,1.0),
                  vec4(-1.5,2.0,0.5,0.0),
                  vec4(0.5,-0.5,0.0,0.0));
    vec4 MP = M*p;
    return dot(T,MP);
}

float CubicInterpolation(vec2 coord){
    vec2 fCoord = fract(coord);
    coord = floor(coord);

    vec4 x;
    vec4 y;
    for(int j = -1; j <=2; j++){
        for(int i = -1; i <=2; i++){
           x[i+1] = Lattice(coord + vec2(float(i),float(j)));
        }
        y[j+1] = CatmullRom(x,fCoord.x);
    }
    return CatmullRom(y,fCoord.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = 16.0;
    vec2 uv = fragCoord/iResolution.xy;
    vec2 coord = uv * scale;
    float noise = CubicInterpolation(coord);
    vec3 col = vec3(noise,noise,noise);
    fragColor = vec4(col,1.0);
}
