#define random(p) fract(sin((p)* vec2(12.9898, 78.233)) * 43758.5453)

float diskIn(vec2 r, vec2 center, float radius, float alpha) {
	return alpha * (1.0 - smoothstep( radius - 0.005, radius + 0.005, length(r-center)));
}

float diskOut(vec2 r, vec2 center, float radius, float outline, float alpha) {
	return alpha * 
        ((1.0 - 
          smoothstep(
              radius + outline - 0.005, 
              radius + outline + 0.005, 
              length(r-center)))
          -
         ( 1. -
          smoothstep( 
              radius - 0.005, 
              radius + 0.005, 
              length(r-center))));
}

float disk(vec2 r, vec2 center, float radius, float alpha, float outline, float outlineAlpha) {
	return diskIn(r, center, radius, alpha) + diskOut(r, center, radius, outline, outlineAlpha);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 col = vec3(0.);

    float nbCircles = 2.;
    float circleLife = 1.;
    float circleRad = 100.;
	vec3 color = vec3(200./255., 50./255., 100./255.);

	for(float i = 0.; i < nbCircles; i++) {
		float timeOffset = i * circleLife/(nbCircles);
        float uid = floor((iTime - timeOffset)/ circleLife) * nbCircles + i;
        //uid = 1. + i;
		float modCircle = mod((iTime - timeOffset)*circleRad/circleLife, circleRad);
        vec2 randPos = random(sqrt(2.)*uid) * (iResolution.xy);
		col = mix(col, color,
                  disk(fragCoord, randPos, modCircle, (1.0 - modCircle/circleRad), mix(5., 0., (modCircle/circleRad)), 1.));

    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
