mat2 xform(float t)
{
    // Some transform stuff from: https://www.shadertoy.com/view/XtXfDS
    float omega = 0.3 * sin(t * 0.37) * sin(t * 0.3);
    float coshOmega = cosh(omega), sinhOmega = sinh(omega);
    return mat2(coshOmega, sinhOmega, sin(t * 0.7)*sinhOmega, (1.0+0.2*cos(t * 0.5))*coshOmega);
}

float dot_at_pos_time(vec2 uv, float t)
{
	uv = xform(t) * uv;

    float at_x = smoothstep(0.0, abs(cos(50.0*uv.x)), 0.57);
    float at_y = smoothstep(0.0, abs(cos(30.0*uv.y)), 0.57);

    float at_x1 = smoothstep(0.0, abs(sin(50.0*uv.x)), 0.57);
    float at_y1 = smoothstep(0.0, abs(sin(30.0*uv.y)), 0.57);

	// Simply Y-axis "blur" (simply adjusting the tightness of the smoothstep)
    float blur = abs((uv.y-0.25)*0.5);

    float col = clamp(smoothstep(0.48-blur, 0.48, at_x*at_y), 0.0 ,1.0);
    float col1 = clamp(smoothstep(0.48-blur, 0.48, at_x1*at_y1), 0.0 ,1.0);

    // Output to screen
    float c = 0.95*col*col1;

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv.x -= 0.5;
	uv.y -= 0.2;

    float omega = 0.3 * sin(iTime * 0.37) * sin(iTime * 0.3);
    float dx = 0.004*cos(2.0*omega);
    float dy = 0.004*sin(2.0*omega);

    // Balck, red, and blue dots
    float blk = 0.4 + 0.6*dot_at_pos_time(uv, 3.0*iTime);
    float b = dot_at_pos_time(vec2(uv.x-dx, uv.y-dy), 3.0*iTime + 0.04);
    float r = dot_at_pos_time(vec2(uv.x+dx, uv.y+dy), 3.0*iTime - 0.04);

    r *= 0.7*(uv.x+0.65);
    b *= 0.7*(uv.y+0.5);

    fragColor = vec4(b*blk, r*blk*b, r*blk, 1.0);
}
