void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 texColor = texture(iChannel0, uv);
    texColor.b = ((texColor.b / 2.0) + (1.0 - ((uv.x + uv.y) /2.0))) * abs(sin(iTime));
    texColor.r = ((texColor.r / 2.0) + (uv.x + uv.y) /2.0) * abs(cos(iTime));
    texColor.g = ((texColor.g / 2.0) + ((uv.x - uv.y)* 2.0)) * abs(sin(iTime)) * cos(iTime);


    // Output to screen
    fragColor = texColor;
}
