renderpass.code:
float DrawC(vec2 uv){

    float d = length(uv);
    float c = smoothstep(.09,.055,d);
    return c;


}

vec2 PathC(vec2 uv,float k, float p, float h, vec2 position,float t) {

    float b = h*((t-p)/k)*sqrt(abs(sin((t-p)/k)));

    uv.y += position.y;
    uv.y +=.1*b;

    uv.x += position.x;
    uv.x -= t*.05;

    return uv;


}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;


    float t = iTime*.9;
    t *= t;

    float k = 1.;
    float p = 20.;
    float h = .3;
    vec2 position = vec2(.5,.5);

    float c = 0.;



    uv =  PathC(uv,k, p, h,position ,t);

    c += DrawC(uv);

    vec3 col = vec3(1.*(sin(t)*.5+.5),.9,.1)*c;










    // Output to screen
    fragColor = vec4(col,1.0);
}
