const float pi = acos(-1.);
const float tau = 2.*pi;

mat2 rot(float a){
    float c=cos(a);float s=sin(a);
    return mat2(c,-s,s,c);
}

float light(vec2 p, float t) {

    p.y *= 2.7;

    float d = length(p);

    p.x *= p.y*7.;

    float a = sin(t*.7 + d*6.5);

    p *= a*abs(sin(t*.3 + d*3.1))*pow(abs(sin(t*.3)), 2.);

    float c = .0005/(sin(length(p*.3)));

    return pow(c, .6);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
    vec2 p = (fragCoord-.5*iResolution.xy)/iResolution.y;

	vec3 col = vec3(0);


    for(int i=0;i<3;i++) {
        col[i] += light(p, -iTime + float(i)*.03);
    }

    p.xy = p.yx;

    for(int i=0;i<3;i++) {
        col[i] += light(p, -iTime + float(i)*.03);
    }

    fragColor = vec4(col, 1.0);
}
