// inspired from python273 https://shadertoy.com/view/4ldyW8
// See also https://www.shadertoy.com/view/ttscRN

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    ivec2 I = ivec2(U+60.*iTime);
    int t = int(iTime) % 3,
        i =   t==1 ? I.x ^ I.y
            : t==2 ? I.x | I.y
            :        I.x & I.y;
                 //  I.x * I.y;

    float v = float(i);  // try also: i*i , i*i*i

    O = vec4( U.x < R.x/2. 
                 ? fract( v / 256.)
                 : fract( v / 13. )
            );
}
