// Fork of "Convolution DFT" by wyatt. https://shadertoy.com/view/tsSfzc
// 2020-08-10 22:56:40

Main {
    float l = length(A(mod(R/R.y*(U-0.5*R),R)).xy);
	Q = 0.5+0.5*sin(log(1.+10.*l)+vec4(1,2,3,4));
	if (iMouse.z>0.&&iMouse.x>U.x)Q = B(U);
}

#define R (iResolution.xy)
#define A(U) texelFetch(iChannel0,ivec2(U),0)
#define B(U) texelFetch(iChannel1,ivec2(U),0)
#define pi 3.14159265359
mat2 e (float t) {
    float c = cos(t),s = sin(t);
	return mat2(c,-s,s,c);
}
#define Main void mainImage( out vec4 Q, in vec2 U )

Main {
	Q = vec4(0);
    Q.xy = vec2(0,exp(-1e-4*dot(U-0.5*R,U-0.5*R)));
    float d = 2.;
    float n = max(4.+4.*sin(.666*iTime),3.);

    for(float i = 1.; i < 8.; i++){
        vec2 w = vec2(0);

        for (float j = 0.; j < 15.; j++) {
            if (j >= n) break;
            float a = d*dot(U-0.5*R,vec2(0,1)*e(j*6.2/n+iTime*sqrt(i)));
        	w += vec2(0,1)*e(a);
        }  
        Q.xy = mat2(Q.xy,-Q.y,Q.x)*w;
        d*=.35+.5/n;
        Q*=.35+.5/n;
    }
    if (length(U-0.5*R)>0.5*R.y) Q*=0.;
}

Main {
	Q = vec4(0);
    vec2 w = 2.*pi*(U-0.5)/R;
    for (float i = 0.; i < R.x; i++) {
        Q.xy += A(vec2(i,U.y)).xy*e(w.x*i)/sqrt(R.x);
    }
}

Main {
	Q = vec4(0);
    vec2 w = 2.*pi*(U-0.5)/R;
    for (float i = 0.; i < R.y; i++) {
        Q.xy += A(vec2(U.x,i)).xy*e(w.y*i)/sqrt(R.y);
   }
}
