void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // central coordinates
    vec2 uv = (fragCoord - iResolution.xy/2.0)/iResolution.x;

    float val = sin(30.0 * length(uv) - 2.0*iTime);

    vec3 col;
    if (val < 0.0) {
        col = vec3(-val / 1.3);
    } else {
        col = (0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4))) * val;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
