float remap01(float a, float b, float t) {
 	return clamp((t-a)/(b-a), 0., 1.);   
}

float remap(float a, float b, float c, float d, float t) {
 	return remap01(a, b, t) * (d-c) + c;  
}

vec2 within(vec2 uv, vec4 rect) {
 	// uv coord within the rect
   	vec2 retval = (uv - rect.xy)/(rect.zw - rect.xy);
    //retval.x = clamp(retval.x, 0., 1.);
    //retval.y = clamp(retval.y, 0., 1.);
    return retval;
}

vec4 eye(vec2 uv) {
    uv -= .5;

    vec4 col = vec4(1.);

    float d = length(uv);

    vec3 irisCol = vec3(.3, .5, 1.);

    col.rgb = mix(col.rgb, irisCol, smoothstep(.1, .7, d)*.5);

    // subtle shadow
    col.rgb *= 1. - smoothstep(.45, .5, d)*.5*clamp((-uv.y+.3), 0., 1.);

    // iris outline
    col.rgb = mix(col.rgb, vec3(0.), smoothstep(.28, .25, d));

    irisCol *= 1. + smoothstep(.3, .05, d);
    col.rgb = mix(col.rgb, irisCol, smoothstep(.28, .20, d));  

    col.rgb = mix(col.rgb, vec3(0.), smoothstep(.16, .14, d));

    float highlight = smoothstep(.1, .09, length(uv - vec2(-.15, .15)));
	highlight += smoothstep(.07, .05, length(uv + vec2(-.08, .08)));
    col.rgb = mix(col.rgb, vec3(1.), highlight);

    col.a = smoothstep(.5, .48, d);

    return col;
}

vec4 mouth(vec2 uv) {
    uv -= .5;
    uv.y *= 1.5;
    uv.y -= uv.x * uv.x * 2.;

    float d = length(uv);

 	vec4 col = vec4(.5, .18, .05, 1.);
    col.a = smoothstep(.5, .48, d);

    float teeth = length(uv - vec2(0., .6));
    vec3 teethCol = vec3(1.) * smoothstep(.6, .35, d);
    col.rgb = mix(col.rgb, teethCol, smoothstep(.4, .37, teeth));

    float tongue = length(uv + vec2(0., .5));
	col.rgb = mix(col.rgb, vec3(1., .5, .5), smoothstep(.5, .3, tongue));

    return col;
}

vec4 head(vec2 uv) {
 	vec4 col = vec4(.9, .65, .1, 1.);
    float d = length(uv);

    col.a = smoothstep(.5, .49, d);

    float edgeShade = remap01(.35, .5, d);
    edgeShade *= edgeShade;

    col.rgb *= 1.0 - edgeShade*.5;
    col.rgb = mix(col.rgb, vec3(.7, .3, .1), smoothstep(.47, .5, d));

    float highlight = smoothstep(.41, .405, d);
    highlight *= remap(.41, -.1, .75, .0, uv.y);
    col.rgb = mix(col.rgb, vec3(1.), highlight);

    d = length(uv - vec2(.25, -.15));
    float cheek = smoothstep(.2, .01, d)*.4;
    col.rgb = mix(col.rgb, vec3(1., .1, .1), cheek);

    return col;
}

vec4 smiley(vec2 uv) {
 	vec4 col = vec4(.0);

    vec4 head = head(uv);
    vec4 eye = eye(within(uv, vec4(.03, -.1, .37, .25)));
    vec4 mouth = mouth(within(uv, vec4(-.3, -.4, .3, -.1)));

    col = mix(col, head, head.a);
    col = mix(col, eye, eye.a);
    col = mix(col, mouth, mouth.a);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	uv -= .5;
    uv.x = abs(uv.x);
    uv.x *= iResolution.x/iResolution.y;
    fragColor = smiley(uv);
}
