void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int number = 20;
    float width = 0.1;
	vec2 st = fragCoord.xy/iResolution.xy;
    vec3 color = vec3(0.0);
    st *= float(number);
    st = fract(st+width/2.0);
    float c = step(width, st.s)*step(width, st.t);
    color = vec3(c);
	fragColor = vec4(color,1.0);
}
