// OG based of"fibonacci flower" by Derzo. https://shadertoy.com/view/ttjGzR

float fib(in vec2 uv)
{
    float m = 1e6;
    for (int x=0; x<200; x++)  
    {
	vec2 p;
    float a = float(x)*radians(132.165);
   //         float a = float(x)*radians(-iTime*.5);

    a += (iTime)*.5;
//	float r = .066*sqrt(float(x-int((sin(iTime)*.5+.5)*190.)));
	float r = float(x-int((sin(iTime)*.5+.5)*190.));
    if (r<0.) continue;
	r = .066*sqrt(r);

    //p = vec2(r*cos(a),r*sin(a));
	/*
	if( length(p - uv) < abs(uv).x*0.06)	
	return 1.;
	if ( length(p - uv) < abs(uv).y*0.06)
	return 1.;         
	*/     
     m = min( m,  length(r*vec2(cos(a),sin(a)) - uv) - max( abs(uv).x, abs(uv).y)*0.06) ;  
    }
    return m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =(2.*fragCoord-iResolution.xy) / iResolution.y;

    fragColor = vec4(0,1,abs(uv.y),1) * smoothstep(1.5/iResolution.y,-1.5/iResolution.y,fib(uv) );
}
