// "Circuit Diagram2" by ntsutae
// https://twitter.com/ntsutae/status/1268820823952916486
// https://www.openprocessing.org/sketch/912094

#define INV_STATES 0.0619f
#define V_FREQ 88882.0f
#define LAYERS 0.075f
#define RANGE 20000.0f

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    float tt = iTime + V_FREQ;

  int x = int(fragCoord.x * 9911.0001f + tt*4.2);
  int y = int(fragCoord.y * LAYERS + tt*42.0);

  float state = texture(iChannel0, fragCoord/iResolution.xy).r;
  int t = int((tt * (INV_STATES + state)));

  vec3 rgb;

  int r = ((x+y)/(y*y))^(x-y);

  rgb.r = float(abs(r*r) % (int(RANGE) + t));
  rgb.g = float(abs(r*r) % (int(RANGE) + t + 1));
  rgb.b = float(abs(r*r) % (int(RANGE) + t + 1 + 1));

  rgb = 1.0f - smoothstep(vec3(RANGE*0.25f), vec3(RANGE*4.0f), rgb);
  rgb = vec3(rgb.r+rgb.g+rgb.b) * 0.333f;
  fragColor = vec4(rgb.grb - state, 1.0);
}
