// #define S(v,r) min( 1., length(v)/p - r )    // lazy AA

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 2.1* ( u+u - R ) / R.y;
    float p = 6./R.y,
          l = length(U), a = -atan(U.x,U.y); // PI/2 tilt

    O = vec4( min(1., abs(fract(l-a/6.283)-.5)/p ) );

    if (U.y < .5) 
        U.y = fract(U.y+.5)-.5,
        O = mix (vec4(1,0,0,1), O, min(1., length(U)/p -3.) );                    
}
