void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 c = texture(iChannel0,uv).rgb;
    c*=vec3(1.,.8,1.2);
    c = mix(vec3(length(c)),c,.6);
    uv=(uv-.5)*1.9;
    c*=max(0.,1.-length(uv*uv*uv));
    fragColor = vec4(c,1.);
}

mat2 rot2D(float a) {
	float s=sin(a);
    float c=cos(a);
    return mat2(c,s,-s,c);
}

vec3 fractal(vec2 p) {
    float t=iTime*.08;
    p*=.35+sin(t)*.15;
    p+=.5;
	vec2 m2 = vec2(1000.);
	float m1 = 1000.;
    for (int i=0; i<15; i++) {
        p=abs(p)/dot(p,p)-1.;
        p*=rot2D(-t);
    	m1=min(m1,dot(p,p));
    	m2=min(m2,abs(p));
    }
    m1=exp(-10.*m1);
    m2=exp(-10.*m2);
    return pow(vec3(m1,m2*rot2D(t*2.)*.8),vec3(10.))*2.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = uv-.5;
	p.x*=iResolution.x/iResolution.y;
	vec3 col = mix(fractal(p),texture(iChannel0,uv).rgb,.993);
	col*=step(mod(iTime,35.),35.);
    fragColor = vec4(col,1.0);
}
