// Whirling Polygons (http://mathworld.wolfram.com/Whirl.html) through repeated linear interpolation.

#define PI2 6.28318530718

float sdSegmentSq( in vec2 p, in vec2 a, in vec2 b )
{
	vec2 pa = p - a, ba = b - a;
    vec2 d = pa - ba * clamp( dot(pa, ba)/dot(ba, ba), 0.0, 1.0 );
	return dot(d, d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
     float h = 1.0 / iResolution.y;
     vec2 aspect = iResolution.xy * h;
     vec2 uv = 2.2 * fragCoord * h - 1.1 * aspect;

     vec3 col = vec3(0.992, 0.965, 0.89);

     vec2 poly[11];

     int N = (int(floor(0.2 * iTime)) & 7) + 3; // number of sides

     for ( int i = 0; i <= N; i++ )
     {
           float th = PI2 * (float(i)/float(N) + 0.3 * iTime);
           poly[i] = vec2(sin(th), cos(th));
     }

     float p = 0.1; // offset
     int M = 6 * N; // nesting level

     float d = 9.0;
     for ( int k = 0; k < M; k++ )
     {
         for ( int i = 0; i < N; i++ )
         {
               d = min( d, sdSegmentSq( uv, poly[i], poly[i + 1] ) );
               poly[i] = mix(poly[i], poly[i + 1], p);
         }
         poly[N] = poly[0];
     }

     col = mix( col, vec3(0.396, 0.48, 0.51), 1.0 - smoothstep(0.0, 0.02 * h, d) );

     col *= 1.0 - 0.3 * length(fragCoord/iResolution.xy - 0.5); // vignetting

     fragColor = vec4(col, 1.0);
}
