#define PI acos(-1.0)
#define PI_2 (2.0*PI)
#define ASPECT (iResolution.x/iResolution.y)

float f(float theta, float r, float t) {
	return r - sin(theta * t * (cos(t/2.0)/2.0 + 0.5))/2.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;
    vec2 o = uv - 0.5;
    o.x *= ASPECT;

    float theta = atan(o.y, o.x) + PI;
    float r = length(o);

    float amplitude = 1.0;
    float period = 1.5/1.0;
    float displacement = 10000.0;
    float t = amplitude*sin(iTime*period) + displacement;

    fragColor = vec4(vec3(f(theta, r, t) < 0.0), 1.0);
}

