vec3 star(vec2 p, vec2 center, float r_min, float r_max, vec3 color) {
    float n = 5.;
    float angle = 2.*3.1415/n;
    vec3 p0 = r_max*vec3(cos(angle/2.),sin(angle/2.),0.);
    vec3 p1 = r_min*vec3(cos(0.),sin(0.),0.);
    vec3 d = p1-p0;
    p -= center;
    float r = sqrt(p.x*p.x+p.y*p.y);
    float ang = atan(p.y,p.x)+3.1415-.4;
    float a = mod(ang,angle)/angle;
    if(a>=.5)
        a = 1.-a;
    vec3 d1 = r*vec3(cos(a*angle),sin(a*angle),0) - p0 - (p.x * .5 + p.y * .3);
    float in_out = step(0.,cross(d,d1).z);
    vec3 col = mix(color, vec3(0.,0.,0.),in_out);
    return col;    
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy / vec2(800, 800)/*iResolution.xy*/;
    uv -= vec2(.45, .23);
    vec2 q = uv * 2.7;

    vec3 col = star(q,vec2(0.,0.),.25,.7,vec3(1.,0.,0.));
    // Output to screen
    fragColor = vec4(col, 1.);
}
